function oger(Message)
year = math.floor(Message / 94608000)
byear = Message % 94608000 
month = math.floor(byear / 7776000)
bmonth = byear % 7776000 
day = math.floor(bmonth / 259200)
bday = bmonth % 259200 
hours = math.floor( bday / 10800)
bhours = bday % 10800 
minx = math.floor(bhours / 60)
sec = math.floor(bhours % 7200) % 60
return string.format("%02d:%02d", minx, sec)
end
